import logo from "./logo.svg";
import data1 from "../src/Data/1.txt";
import "./App.css";
import { useEffect } from "react";

function App() {
  // var file = data1.files[0]; // assuming single file, no multiple
  useEffect(() => {
    var file = data1.files[0];
    var reader = new FileReader();
    reader.onload = function (e) {
      var text = reader.result; // the entire file
      console.log(text);
      // var firstLine = text.split("\n").shift(); // first line
      // console.log(firstLine); // use the console for debugging
    };
  }, []);

  return <div className="App"></div>;
}

export default App;
